mapper.select:
  		SELECT * FROM
		    (SELECT rownum R, B.* FROM 
		        (SELECT * FROM qnas WHERE 
		        	NUM>0 
				AND
					PRODUCT_NUM = #{product_num} 
				AND 
					QNA_STEP = 0
		            ORDER BY QNA_REF DESC, QNA_STEP ASC
		         ) B
		     )
		WHERE R between #{startRow} and #{lastRow}
